mapper.update:
 			 update tbrd_mst 
 			    set TITL = #{TITL}
 			      , CNTN = #{CNTN}
 			      , WRTR = #{WRTR}
 			      , REG_DTTM = NOW()
 			      , MOD_DTTM = NOW()
			  where SEQ = #{SEQ}

mapper.insert:
insert into tbrd_mst 
	SEQ = #{SEQ}
   TITL = #{TITL}
 , CNTN = #{CNTN}
 , WRTR = #{WRTR}
 , REG_DTTM = NOW()
 , MOD_DTTM = NOW()
